window.onload=()=>{if("speechSynthesis"in window){let demo=document.getElementById("demo"),vlist=document.getElementById("demo-voice"),vvol=document.getElementById("demo-vol"),vpitch=document.getElementById("demo-pitch"),vrate=document.getElementById("demo-rate"),vmsg=document.getElementById("demo-msg"),vgo=document.getElementById("demo-go");var voices=()=>{speechSynthesis.getVoices().forEach((v,i)=>{let opt=document.createElement("option");opt.value=i,opt.innerHTML=v.name,vlist.appendChild(opt)})};voices(),speechSynthesis.onvoiceschanged=voices;var speak=()=>{let msg=new SpeechSynthesisUtterance;return msg.voice=speechSynthesis.getVoices()[vlist.value],msg.text=vmsg.value,speechSynthesis.speak(msg),!1};demo.onsubmit=speak,vlist.disabled=!1,vvol.disabled=!1,vpitch.disabled=!1,vrate.disabled=!1,vmsg.disabled=!1,vgo.disabled=!1}else alert("Text-to-speech is not supported on your browser!")};